migration_templates.safe_migration_template:
-- Migration: {{migration_name}}
-- Description: {{migration_description}}
-- Date: {{migration_date}}
-- Safety Level: {{safety_level}}

BEGIN;

-- Pre-migration validation
DO $$
BEGIN
  -- Verify prerequisites
  IF NOT EXISTS (SELECT 1 FROM information_schema.tables WHERE table_name = 'prerequisite_table') THEN
    RAISE EXCEPTION 'Prerequisites not met for migration';
  END IF;
END $$;

-- Create savepoint for rollback capability
SAVEPOINT migration_start;

-- Migration operations
{{migration_operations}}

-- Post-migration validation
DO $$
BEGIN
  -- Verify migration success
  IF NOT EXISTS (SELECT 1 FROM information_schema.tables WHERE table_name = '{{target_table}}') THEN
    RAISE EXCEPTION 'Migration validation failed';
  END IF;
END $$;

-- Log migration completion
INSERT INTO schema_migrations_log (migration_name, applied_at, status) 
VALUES ('{{migration_name}}', NOW(), 'completed');

COMMIT;

migration_templates.table_creation_template:
-- Create table with proper RLS and policies
CREATE TABLE {{table_name}} (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  {{table_fields}},
  created_at TIMESTAMPTZ DEFAULT NOW(),
  updated_at TIMESTAMPTZ DEFAULT NOW()
);

-- Enable Row Level Security
ALTER TABLE {{table_name}} ENABLE ROW LEVEL SECURITY;

-- Create RLS policies
CREATE POLICY "{{table_name}}_select_policy" ON {{table_name}}
  FOR SELECT USING ({{select_condition}});

CREATE POLICY "{{table_name}}_insert_policy" ON {{table_name}}
  FOR INSERT WITH CHECK ({{insert_condition}});

CREATE POLICY "{{table_name}}_update_policy" ON {{table_name}}
  FOR UPDATE USING ({{update_condition}});

-- Create updated_at trigger
CREATE TRIGGER update_{{table_name}}_updated_at 
  BEFORE UPDATE ON {{table_name}}
  FOR EACH ROW EXECUTE FUNCTION update_updated_at_column();

-- Create indexes
{{table_indexes}}

migration_templates.function_creation_template:
-- Create or replace function with proper error handling
CREATE OR REPLACE FUNCTION {{function_name}}({{function_params}})
RETURNS {{return_type}}
LANGUAGE plpgsql
SECURITY DEFINER
AS $$
DECLARE
  {{function_variables}}
BEGIN
  -- Input validation
  {{input_validation}}

  -- Main function logic
  {{function_body}}

  -- Return result
  RETURN {{return_value}};

EXCEPTION
  WHEN OTHERS THEN
    -- Log error for debugging
    INSERT INTO error_logs (function_name, error_message, created_at)
    VALUES ('{{function_name}}', SQLERRM, NOW());
    RAISE;
END $$;

-- Grant appropriate permissions
GRANT EXECUTE ON FUNCTION {{function_name}}({{function_params}}) TO authenticated;

migration_templates.data_migration_template:
-- Safe data migration with batching
DO $$
DECLARE
  batch_size INTEGER := 1000;
  processed_count INTEGER := 0;
  total_count INTEGER;
BEGIN
  -- Get total count for progress tracking
  SELECT COUNT(*) INTO total_count FROM {{source_table}} WHERE {{condition}};

  RAISE NOTICE 'Starting data migration: % records to process', total_count;

  -- Process in batches
  LOOP
    -- Update batch
    WITH batch AS (
      SELECT id FROM {{source_table}} 
      WHERE {{condition}} 
      LIMIT batch_size
      FOR UPDATE
    )
    UPDATE {{source_table}} SET {{update_operations}}
    FROM batch WHERE {{source_table}}.id = batch.id;

    GET DIAGNOSTICS processed_count = ROW_COUNT;
    EXIT WHEN processed_count = 0;

    -- Progress logging
    RAISE NOTICE 'Processed % records', processed_count;

    -- Small delay to avoid overwhelming the system
    PERFORM pg_sleep(0.1);
  END LOOP;

  RAISE NOTICE 'Data migration completed successfully';
END $$;

rollback_procedures.rollback_script_template:
-- Rollback script for: {{migration_name}}
-- Generated: {{generation_time}}

BEGIN;

-- Reverse migration operations
{{rollback_operations}}

-- Restore previous state validation
{{validation_checks}}

-- Log rollback completion
INSERT INTO schema_migrations_log (migration_name, applied_at, status)
VALUES ('{{migration_name}}_rollback', NOW(), 'rolled_back');

COMMIT;
