async {{methodName}}({{parameters}}): Promise<{{returnType}}> {
  try {
    // Check cache first
    const cacheKey = `{{methodName}}_${JSON.stringify(arguments)}`;
    const cached = this.getCachedData(cacheKey);
    if (cached) return cached;

    // Perform operation
    const result = await this.{{operationName}}({{parameters}});

    // Cache result
    this.setCachedData(cacheKey, result);

    return result;
  } catch (error) {
    logger.error('{{ServiceName}}.{{methodName}} failed:', error);
    throw new Error(`Failed to {{operation description}}: ${error.message}`);
  }
}
